package Inputoutput;

// &, |, ^


public class BitTwo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 13, b = 7;
		System.out.println("a & b :"+(a & b)); // ("문자열 :"+(처리연산))
		System.out.println("a | b :"+(a | b));  // ("문자열 :"+(처리연산))
		System.out.println("a ^ b :"+(a ^ b)); // 프린트인("문자열 :"+())
		
	}

}
