package Inputoutput;

//논리 연산자 : &&, ||, !
// 순수 논리 연산자로, 결과는 불린boolean으로 나온다.
public class LogicEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean a = true, b = false;
		
		System.out.println("a && b :" +(a && b));
		System.out.println("a || b :" +(a || b));
		System.out.println("!a :" +!a);

		
	}

}
