package Inputoutput;

public class dddd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		int res = 0;
		
		
		// 복합 대입 연산자
		
		res += a; //res = res +a
		System.out.println("res :"+ res);
		res -= a; // res = res -a
		System.out.println("res :"+ res);
		res *= a; // res = res * a
		System.out.println("res :"+ res);
		res /= a; // res = res / a
		System.out.println("res :"+ res);
		res %= a; // res = 
		System.out.println("res :"+ res);
		
		
		
		
		
	}

}
