package solvingEx;

public class Transfer {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*캐릭터(문자 하나 받는 자료형)를 스트링(문자열을 받는 자료형)으로 변환하려면
		 * 
		 * 
		 * 
		 */
		char c = 'a'; // 캐릭터 변수 명칭 = '문자 하나';
		
		String s = String.valueOf(c); // String 변수 명칭 = String.valueOf(캐릭터 변수 명칭);
	System.out.println(s); // 출력 기호 작성하여 확인해 보자! System.out.println(스트링 변수 명칭);


	}

}
